package Javac;

public class Length {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] a = { 1, 2, 3, 4, 5 };
		System.out.println(a.length);//only for arrays
		String[] names = { "Manoj", "Gagan", "Teju", "Bhavani" };
		String gf="u r so hot";
		System.out.println(gf.length());
		System.out.println(names.length);//only for string arrays
	}

}
